#ifndef __TYPES_H__
#define __TYPES_H__

#ifndef s8
    typedef char            s8;
#endif
#ifndef s16
    typedef short           s16;
#endif
#ifndef s32
    typedef int             s32;
#endif
#ifndef u8
    typedef unsigned char   u8;
#endif
#ifndef u16
    typedef unsigned short  u16;
#endif
#ifndef u32
    typedef unsigned int    u32;
#endif

#endif // __TYPES_H__
